pub struct CubeDisplay<'a> {
    tilemap: &'a TileMap,
    kind: DisplayKind,
    render_hidden_faces: bool,
}

impl<'tm> CubeDisplay<'tm> {
    pub fn vt(tilemap: &'tm TileMap) -> CubeDisplay<'tm> {
        CubeDisplay {
            tilemap,
            kind: DisplayKind::VT,
            render_hidden_faces: true,
        }
    }
    pub fn svg(tilemap: &'tm TileMap) -> CubeDisplay<'tm> {
        CubeDisplay {
            tilemap,
            kind: DisplayKind::SVG,
            render_hidden_faces: true,
        }
    }
    pub fn without_hidden_faces(self) -> CubeDisplay<'tm> {
        CubeDisplay {
            render_hidden_faces: false,
            ..self
        }
    }
}

impl fmt::Display for CubeDisplay<'_> {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        match self.kind {
            DisplayKind::VT => self.display_vt(f),
            DisplayKind::SVG => self.display_svg(f),
        }
    }
}
